import { createApp } from "vue";
import App from "../view/striff-report.vue";
import LoadScript from "vue-plugin-load-script";

const app = createApp(App);
const STRIFF_API_URL = "http://localhost:8080/v1/github/striffs";

app.use(LoadScript);
app.mount("#app");

export function getPRInfo() {
  return new Promise((Resolve, reject) => {
    chrome.runtime.sendMessage({ msg: ["getPRInfo"] }, function (prInfoObj) {
      if (Object.keys(prInfoObj).length === 0) {
        reject(
          new Error(
            "Oops! An internal error occurred while fetching Pull Request data from GitHub."
          )
        );
      } else {
        Resolve(prInfoObj);
      }
    });
  });
}

export function fetchStriff(prInfoObj) {
    console.log(prInfoObj);
  return new Promise(() => {
    var striffReqURL =
      STRIFF_API_URL +
      "/owners/" +
      prInfoObj["repoOwner"] +
      "/repos/" +
      prInfoObj["repoName"] +
      "/pulls/" +
      prInfoObj["pullNo"] +
      "?gh_hostname=" +
      prInfoObj["ghHost"];
    const request = new Request(encodeURI(striffReqURL));
    const headerObj = {
      Accept: "application/json",
      Authorization: "token " + prInfoObj["token"],
    };
    fetch(request, {
      method: "GET",
      headers: headerObj,
    }).then((response) => console.log(response.json()))
      .catch(function (err) {
        console.log(err);
      });
  });
}

export function parseResponse(striffAPIResp) {
  console.log("HERE");
  console.log(striffAPIResp);
  return new Promise((Resolve) => {
    Resolve(
      striffAPIResp.json().then((data) => ({
        status: striffAPIResp.status,
        body: data,
      }))
    );
  });
}

export function processStriffResponse(response) {
  return new Promise((Resolve, reject) => {
    if (response.status > 200) {
      reject(new Error(response.body.errorMessage));
    } else {
      if (
        "striffs" in response.body &&
        Object.keys(response.body.striffsMap).length > 0
      ) {
        Resolve(response.body);
      } else {
        // No structural changes found
        reject(new Error("No structural changes were found!"));
      }
    }
    document.getElementById("striff_btn").disabled = true;
  });
}
