import { createApp } from "vue";
import App from "../view/striff-report.vue";
import LoadScript from "vue-plugin-load-script";

const app = createApp(App);
app.use(LoadScript);
app.mount("#app");

export function getPRInfo() {
  return new Promise((Resolve, reject) => {
    chrome.runtime.sendMessage({ msg: ["getPRInfo"] }, function (response) {
        if (response.length < 1) {
            reject("Oops! An internal error occurred while fetching Pull Request data from GitHub, our team is working on it.")

      }
    });
  });
}

