import { createApp } from "vue";
import App from "../view/striff-report.vue";
import LoadScript from "vue-plugin-load-script";

const app = createApp(App);
const STRIFF_BOT_GITHUB_API = "https://api.striff.io/v1/github/striffs";

app.use(LoadScript);
app.mount("#app");

export function getPRInfo() {
  return new Promise((Resolve, reject) => {
    chrome.runtime.sendMessage({ msg: ["getPRInfo"] }, function (prInfoObj) {
      if (Object.keys(prInfoObj).length === 0) {
        reject(
          "Oops! An internal error occurred while fetching Pull Request data from GitHub."
        );
      } else {
        Resolve(prInfoObj);
      }
    });
  });
}

export function fetchStriff(prInfoObj) {
  return new Promise((Resolve) => {
    var striffReqURL =
      STRIFF_BOT_GITHUB_API +
      "/owners/" +
      prInfoObj["repoOwner"] +
      "/repos/" +
      prInfoObj["repoName"] +
      "/pulls/" +
      prInfoObj["pullNo"] +
      "?gh_hostname=" +
      prInfoObj["ghHost"];
    const request = new Request(encodeURI(striffReqURL));
    const headerObj = {
      Accept: "application/json",
      Authorization: "token " + prInfoObj["token"],
    };
    Resolve(
      fetch(request, {
        method: "GET",
        headers: headerObj,
      })
    );
  });
}

export function parseResponse(striffAPIResp) {
  return new Promise((Resolve) => {
    Resolve(
      striffAPIResp.json().then((data) => ({
        status: response.status,
        body: data,
      }))
    );
  });
}

export function processStriffResponse(response) {
  return new Promise((Resolve, reject) => {
    if (response.status > 200) {
      reject(response.body.errorMessage);
    } else {
      if (
        "striff" in response.body &&
        Object.keys(response.body.striffsMap).length > 0
      ) {
        updateStriffBtn(
          "Striffs generated",
          "Striff diagrams were successfully generated.",
          "success"
        );
        displayStriffs(response.body);
      } else {
        // No structural changes found
        updateStriffBtn(
          "No changes found...",
          "No structural changes were detected in this pull request.",
          "no_change"
        );
      }
    }
    document.getElementById("striff_btn").disabled = true;
  });
}

export function displayStriffs() {
  return new Promise((Resolve, reject) => {
    if (number % 2 == 0) {
      Resolve("The number is even");
    } else {
      reject("The number is odd");
    }
  });
}

export function createOnclickElements() {
  return new Promise((Resolve, reject) => {
    if (number % 2 == 0) {
      Resolve("The number is even");
    } else {
      reject("The number is odd");
    }
  });
}
